import{_ as s,c as a,o as e,O as n}from"./chunks/framework.d7e37bdc.js";const y=JSON.parse('{"title":"环境搭建","description":"","frontmatter":{},"headers":[],"relativePath":"rust/install.md","filePath":"rust/install.md","lastUpdated":1683677121000}'),l={name:"rust/install.md"},t=n(`<h1 id="环境搭建" tabindex="-1">环境搭建 <a class="header-anchor" href="#环境搭建" aria-label="Permalink to &quot;环境搭建&quot;">​</a></h1><p>因为要学习 Rust，所以这里我推荐使用<a href="https://www.rust-lang.org/zh-CN/tools/install" target="_blank" rel="noreferrer">官网教程</a>来安装最新版本。</p><h2 id="windows" tabindex="-1">Windows <a class="header-anchor" href="#windows" aria-label="Permalink to &quot;Windows&quot;">​</a></h2><p>Windows 可以使用两种安装方式，一种是 WSL。一种是原生安装。</p><p>WSL 执行如下命令就可以安装了：</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">curl</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">--proto</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">=https</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">--tlsv1.2</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-sSf</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">https://sh.rustup.rs</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">|</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">sh</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>如果是原生安装，则首先需要下载 <a href="https://www.rust-lang.org/zh-CN/tools/install" target="_blank" rel="noreferrer">RUST_INIT.EXE</a>。这里推荐 64 位版本。需要安装 Visual Studio，这里不需要单独下载，遵循安装过程中的提示即可。</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>编译时如果出现：<code>linker link.exe not found</code></p><p>执行如下命令即可解决：</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">rustup</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">toolchain</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">install</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">stable-x86_64-pc-windows-gnu</span></span>
<span class="line"><span style="color:#FFCB6B;">rustup</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">default</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">stable-x86_64-pc-windows-gnu</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div></div><h2 id="ide" tabindex="-1">IDE <a class="header-anchor" href="#ide" aria-label="Permalink to &quot;IDE&quot;">​</a></h2><p>这里推荐使用 VS Code。免费。好用。</p><p>另外还需要安装 <a href="https://marketplace.visualstudio.com/items?itemName=rust-lang.rust-analyzer" target="_blank" rel="noreferrer">rust-analyzer</a> 扩展。</p>`,11),o=[t];function p(r,c,i,d,u,C){return e(),a("div",null,o)}const _=s(l,[["render",p]]);export{y as __pageData,_ as default};
